import { useState } from 'react';
import { Button } from '@/components/ui/button';
import PptxGenJS from 'pptxgenjs';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import { Download, FileText, Loader2 } from 'lucide-react';

interface ExportButtonsProps {
  className?: string;
}

export default function ExportButtons({ className }: ExportButtonsProps) {
  const [isExporting, setIsExporting] = useState(false);
  const [exportType, setExportType] = useState<'pdf' | 'pptx' | null>(null);

  const captureSlides = async () => {
    const slideContainer = document.querySelector('.slide');
    if (!slideContainer) {
      throw new Error('슬라이드 컨테이너를 찾을 수 없습니다.');
    }

    const totalSlides = 20; // 총 슬라이드 수
    const images: string[] = [];

    // 현재 슬라이드 상태 저장
    const currentSlide = parseInt(slideContainer.getAttribute('data-current-slide') || '1');

    for (let i = 1; i <= totalSlides; i++) {
      // 슬라이드 변경 이벤트 발생
      const event = new CustomEvent('slideChange', { detail: { slideNumber: i } });
      document.dispatchEvent(event);

      // 렌더링 대기
      await new Promise(resolve => setTimeout(resolve, 500));

      // 현재 보이는 슬라이드 캡처
      const canvas = await html2canvas(slideContainer as HTMLElement, {
        width: 1920,
        height: 1080,
        scale: 0.8,
        backgroundColor: '#ffffff',
        useCORS: true,
        allowTaint: true
      });

      images.push(canvas.toDataURL('image/png'));
    }

    // 원래 슬라이드로 복원
    const restoreEvent = new CustomEvent('slideChange', { detail: { slideNumber: currentSlide } });
    document.dispatchEvent(restoreEvent);

    return images;
  };

  const exportToPDF = async () => {
    setIsExporting(true);
    setExportType('pdf');

    try {
      const images = await captureSlides();
      const pdf = new jsPDF('l', 'mm', [297, 210]); // A4 landscape

      images.forEach((image, index) => {
        if (index > 0) {
          pdf.addPage();
        }
        pdf.addImage(image, 'PNG', 0, 0, 297, 210);
      });

      pdf.save('SWDP-AI-Framework-Presentation.pdf');
    } catch (error) {
      console.error('PDF export failed:', error);
    } finally {
      setIsExporting(false);
      setExportType(null);
    }
  };

  const exportToPPTX = async () => {
    setIsExporting(true);
    setExportType('pptx');

    try {
      const images = await captureSlides();
      const pptx = new PptxGenJS();

      images.forEach((image) => {
        const slide = pptx.addSlide();
        slide.addImage({
          data: image,
          x: 0,
          y: 0,
          w: '100%',
          h: '100%'
        });
      });

      await pptx.writeFile({ fileName: 'SWDP-AI-Framework-Presentation.pptx' });
    } catch (error) {
      console.error('PPTX export failed:', error);
    } finally {
      setIsExporting(false);
      setExportType(null);
    }
  };

  return (
    <div className={`flex gap-2 ${className}`}>
      <Button
        onClick={exportToPDF}
        disabled={isExporting}
        variant="outline"
        size="sm"
      >
        {isExporting && exportType === 'pdf' ? (
          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
        ) : (
          <FileText className="w-4 h-4 mr-2" />
        )}
        PDF 내보내기
      </Button>

      <Button
        onClick={exportToPPTX}
        disabled={isExporting}
        variant="outline"
        size="sm"
      >
        {isExporting && exportType === 'pptx' ? (
          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
        ) : (
          <Download className="w-4 h-4 mr-2" />
        )}
        PPTX 내보내기
      </Button>
    </div>
  );
}

